class Solution {
public:
  long long minimumCost(int m, int n, vector<int> &hc, vector<int> &vc) {
    sort(hc.begin(), hc.end());
    sort(vc.begin(), vc.end());

    reverse(hc.begin(), hc.end());
    reverse(vc.begin(), vc.end());

    long long hi = 0, vi = 0, hs = 1, vs = 1;
    long long total = 0;

    long long x = 0;
    vector<long long> temp1, temp2;

    while (vi < n - 1 && hi < m - 1) {
      if (hc[hi] > vc[vi]) {
        total += 1LL * hc[hi] * vs;
        hs++;
        hi++;
      } else {
        total += 1LL * vc[vi] * hs;
        vs++;
        vi++;
      }
    }

    while (hi < m - 1) {
      total += 1LL * hc[hi] * vs;
      hs++;
      hi++;
    }

    while (vi < n - 1) {
      total += 1LL * vc[vi] * hs;
      vs++;
      vi++;
    }

    return total;
  }
};
