class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hC, vector<int>& vC) {
        sort(hC.begin(), hC.end(), greater<int>());
        sort(vC.begin(), vC.end(), greater<int>());

        int h_cut = 1, v_cut = 1;
        int i=0, j=0;

        long long cost = 0;

        while(i<m-1 && j<n-1) {
            if(hC[i] > vC[j]) {
                cost += (hC[i] * 1ll * v_cut);
                i++;
                h_cut++;
            }
            else {
                cost += (vC[j] * 1ll * h_cut);
                j++;
                v_cut++;
            }
        }

        while(i<m-1) {
            cost += (hC[i] * 1ll * v_cut);
            i++;
        }

        while(j<n-1) {
            cost += (vC[j] * 1ll * h_cut);
            j++;
        }

        return cost;
    }
};
