class Solution:
    def minimumCost(self, m: int, n: int, hc: List[int], vc: List[int]) -> int:

        # def dfs()

        h,v = 1,1
        heap = []
        for i in hc:
            heappush(heap,(-i,0))
        for i in vc:
            heappush(heap,(-i,1))

        ans = 0
        while heap:
            c,i = heappop(heap)
            c=-c
            if i==0:
                ans+=c*v
                h+=1
            else:
                ans+=c*h
                v+=1
        return ans
