class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.rbegin(),h.rend());
        sort(v.rbegin(),v.rend());
        long long i=0;//h
        long long j=0;//v
        long long vp=1;
        long long hp=1;
        long long ans=0;
        m--;n--;
        while(i<m && j<n){
            if(h[i]>v[j]){
                ans+=1LL*vp*h[i];
                hp++;
                i++;
            }else{
                ans+=1LL*hp*v[j];
                vp++;
                j++;
            }
        }
        while(i<m){
            ans+=1LL*vp*h[i];
            hp++;
            i++;
        }
        while(j<n){
            ans+=1LL*hp*v[j];
            vp++;
            j++;
        }
        return ans;
    }
};
