class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hr, vector<int>& vr) {
        int hc=0, vc=0;
        vector<pair<int,int>> tot;
        pair<int,int> pr;
        for(int i=0; i<(m-1); ++i){
            pr.first = hr[i];
            pr.second = 1;
            tot.push_back(pr);
        }
        for(int i=0; i<(n-1); ++i){
            pr.first=vr[i];
            pr.second=0;
            tot.push_back(pr);
        }
        sort(tot.begin(), tot.end());
        long long ans=0;
        for(int i=(m+n-3); i>=0; --i){
            if(tot[i].second == 1){
                ans += (long long)(tot[i].first)*(vc+1);
                hc++;
            } else {
                ans += (long long)(tot[i].first)*(hc+1);
                vc++;
            }
        }
        return ans;
    }
};
