class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(), h.end(), greater<int>());
        sort(v.begin(), v.end(), greater<int>());
        long long hc=1, vc=1;
        int i=0, j=0;
        long long res=0;
        while (i<m-1 && j<n-1) {
            if (h[i]>v[j]){
                res += h[i] * vc * 1ll;
                hc++;
                i++;
            }
            else {
                res += v[j] * hc * 1ll;
                vc++;
                j++;
            }
        }
        int total = 0;
        while (i < m-1)
            total += h[i++]*1ll;
        res += total * vc;

        total = 0;
        while (j < n-1)
            total += v[j++]*1ll;
        res += total * hc;
        return res;
    }
};
