class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        max_heap = []
        for h in horizontalCut:
            heapq.heappush(max_heap, (-h, 'h'))
        for v in verticalCut:
            heapq.heappush(max_heap, (-v, 'v'))

        h_pieces, v_pieces = 1, 1 
        total_cost = 0

        while max_heap:
            cost, cut_type = heapq.heappop(max_heap)
            if cut_type == 'h':
                total_cost += -1*cost * v_pieces
                h_pieces += 1
            else:
                total_cost += -1*cost * h_pieces
                v_pieces += 1

        return total_cost
