class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(hc.begin() , hc.end());
        sort(vc.begin() , vc.end());
        reverse(hc.begin() , hc.end());
        reverse(vc.begin() , vc.end());
        int ho = 1 , vt = 1;
        int i = 0 , j = 0;
        long long ans = 0;
        while (i < m - 1 && j < n - 1){
            if (hc[i] >= vc[j]){
                ans += hc[i] * vt;
                ho++;
                i++;
            }
            else{
                ans += vc[j] * ho;
                vt++;
                j++;
            }
        }
        long long total = 0;
        while (i < m - 1) total += hc[i++];
        ans += total * vt;
        total = 0;
        while (j < n - 1) total += vc[j++];
        ans += total * ho;
        return ans;
    }
};
