class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        // KARTIK JOSHI
        if (m == 1 && n == 1) return 0;
        vector<pair<long long, char>> v;
        for (int i = 0; i < m-1; ++i) {
            v.push_back({hc[i], 'h'});
        }
        for (int i = 0; i < n-1; ++i) {
            v.push_back({vc[i], 'v'});
        }
        sort(v.rbegin(), v.rend());
        long long hcuts = 0, vcuts = 0, res = 0;
        for (auto i: v) {
            if (i.second == 'h') {
                res += (i.first * (vcuts+1));
                ++hcuts;
            } else {
                res += (i.first * (hcuts+1));
                ++vcuts;
            }
        }
        return res;
    }
};
