class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        int hn=1,vn=1;
        int hs = horizontalCut.size(), vs = verticalCut.size();
        sort(horizontalCut.begin(),horizontalCut.end());
        sort(verticalCut.begin(),verticalCut.end());
        int i=hs-1,j=vs-1;
        long long ans=0;
        while(i>=0&&j>=0){
            if(horizontalCut[i]>verticalCut[j]){
                ans+=horizontalCut[i]*vn;
                hn++;
                i--;
            }else{
                ans+=verticalCut[j]*hn;
                vn++;
                j--;
            }
        }
        if(i>=0){
            for(;i>=0;i--){
                ans+=horizontalCut[i]*vn;
            }
        }
        if(j>=0){
            for(;j>=0;j--){
                ans+=verticalCut[j]*hn;
            }
        }
        return ans;
    }
};
