class Solution:
    def minimumCost(self, m: int, n: int, hc: List[int], vc: List[int]) -> int:
        hp , vp = 1,1

        s = [[i,"h"] for i in hc] + [[j,"v"] for j in vc]
        s.sort(key=lambda x:-x[0])
        ans=0

        for i in s:
            if i[1]=="v":
                ans += i[0]*hp
                vp+=1
            elif i[1]=="h":
                ans+= i[0]*vp
                hp+=1
        return ans
