class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
         sort(h.begin(),h.end());
        sort(v.begin(),v.end());
        int i= m-2;
        int j= n-2;
        long long ans=0;
        while(i>=0 || j>=0 ){
            if(i<0 ){
                ans+= v[j]*((m-2)- i +1);
                j--;
            }
            else if(j<0){
                ans+= h[i]*((n-2)- j +1);
                i--;
            }
            else if(v[j]>=h[i]){
                ans+= v[j]*((m-2)- i +1);
                j--;
            }
            else{
                ans+= h[i]*((n-2)- j +1);
                i--;
            }
        }
        return ans;
    }
};
