class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        ans = 0
        arr = [[-i, 0] for i in horizontalCut]
        for i in verticalCut:
            arr.append([-i, 1])
        arr.sort()
        horizontalCount = 1
        verticalCount = 1
        for cost, cutType in arr:
            if cutType==0:
                ans += -cost*verticalCount
                horizontalCount+=1
            else:
                ans+= -cost*horizontalCount
                verticalCount+=1
        return ans
