class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(), h.end(), greater<int>());
        sort(v.begin(), v.end(), greater<int>());

        long long hp = 1, vp = 1, ans = 0;
        int i = 0, j = 0;

        while (i < h.size() && j < v.size()) {
            if (h[i] > v[j]) {
                ans += h[i] * vp;
                hp++;
                i++;
            } else {
                ans += v[j] * hp;
                vp++;
                j++;
            }
        }

        while (i < h.size()) {
            ans += h[i] * vp;
            hp++;
            i++;
        }

        while (j < v.size()) {
            ans += v[j] * hp;
            vp++;
            j++;
        }

        return ans;

    }
};
