class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        vector<pair<int, char>> t;
        for (auto i : h) {
            t.emplace_back(i, 'H');
        }
        for (int i : v) {
            t.emplace_back(i, 'V');
        }
        sort(t.rbegin(), t.rend());

        long long answer = 0;
        int vert = 1, hor = 1;
        for (auto p : t) {
            if (p.second == 'H') {
                answer += p.first * hor;
                vert += 1;
            } else {
                answer += p.first * vert;
                hor += 1;
            }
        }
        return answer;
    }
};
