class Solution:
    def minimumCost(self, m: int, n: int, h: List[int], v: List[int]) -> int:
        nh = nv = 1
        h.sort()
        v.sort()
        s = 0
        i , j = len(h) - 1, len(v) - 1
        while i >= 0 and j >= 0:
            if h[i] * nv + v[j] * (nh+1) < h[i] * (nv+1) + v[j] * nh:
                s += h[i] * nv
                nh += 1
                i -= 1
            else:
                s += v[j] * nh
                nv += 1
                j -= 1
        while i >= 0:
            s += h[i] * nv
            i -= 1
        while j >= 0:
            s += v[j] * nh
            j -= 1
        return s
