class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        long long i = 0, j = 0, k = 0, ans = 0;
        sort(horizontalCut.rbegin(), horizontalCut.rend());
        sort(verticalCut.rbegin(), verticalCut.rend());
        horizontalCut.push_back(0);
        verticalCut.push_back(0);
        for (; i < m && j < n;) {
            if (horizontalCut[i] < verticalCut[j]) ans += (i + 1) * verticalCut[j++];
            else ans += (j + 1) * horizontalCut[i++];
        }
        return ans;
    }
};
