class Solution {
    public long minimumCost(int m, int n, int[] horizontalCut, int[] verticalCut) {
        Arrays.sort(horizontalCut);
        Arrays.sort(verticalCut);

        int hIndex = horizontalCut.length - 1;
        int vIndex = verticalCut.length - 1;
        int hSegments = 1;
        int vSegments = 1;
        long totalCost = 0;

        while (hIndex >= 0 && vIndex >= 0) {
            if (horizontalCut[hIndex] >= verticalCut[vIndex]) {
                totalCost += horizontalCut[hIndex] * vSegments;
                hSegments++;
                hIndex--;
            } else {
                totalCost += verticalCut[vIndex] * hSegments;
                vSegments++;
                vIndex--;
            }
        }

        while (hIndex >= 0) {
            totalCost += horizontalCut[hIndex] * vSegments;
            hSegments++;
            hIndex--;
        }

        while (vIndex >= 0) {
            totalCost += verticalCut[vIndex] * hSegments;
            vSegments++;
            vIndex--;
        }

        return totalCost;
    }
}
