#define ll long long
#define ii pair<int,int>
#define iii pair<int, ii>
#define fi first
#define se second
#define pb push_back
const int H = 0, V = 1;

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<iii> cost;
        for(int i=0;i<horizontalCut.size();i++) cost.pb({horizontalCut[i], {i, H}});
        for(int i=0;i<verticalCut.size();i++) cost.pb({verticalCut[i], {i, V}});
        sort(cost.begin(), cost.end());
        vector<bool> isVcut(verticalCut.size(), 0);
        vector<bool> isHcut(horizontalCut.size(), 0);
        ll ans = 0;
        int vcut = 1, hcut = 1;
        for(int i=cost.size()-1;i>=0;i--) {
            // ans += cost[i].fi;
            int mul = 1;
            if(cost[i].se.se == H) {
                // for(int j=0;j<isVcut.size();j++) {
                //     if(isVcut[j]) mul++;
                // }
                mul = max(mul, vcut);
            } else {
                // for(int j=0;j<isHcut.size();j++) {
                //     if(isHcut[j]) mul++;
                // }
                mul = max(mul, hcut);
            }
            // cout<<"cut "<<(cost[i].se.fi == H? "H":"V")<<" "<<cost[i].fi<<"*"<<mul<<"\n";
            if(cost[i].se.se == H) hcut++;//isHcut[cost[i].se.fi] = 1;
            else vcut++;//isVcut[cost[i].se.fi] = 1;

            ans += (ll)(cost[i].fi) * (ll)(mul);
        }
        return ans;
    }
};
