#define ll long long

class Solution {
public:
    ll minimumCost(ll m, ll n, std::vector<int>& horizontalCut, std::vector<int>& verticalCut) {
        std::sort(horizontalCut.rbegin(), horizontalCut.rend());
        std::sort(verticalCut.rbegin(), verticalCut.rend());

        ll totalCost = 0;
        ll hPieces = 1, vPieces = 1;
        ll hIndex = 0, vIndex = 0;

        while (hIndex < horizontalCut.size() || vIndex < verticalCut.size()) {
            if (hIndex < horizontalCut.size() && (vIndex >= verticalCut.size() || horizontalCut[hIndex] >= verticalCut[vIndex])) {
                totalCost += horizontalCut[hIndex] * vPieces;
                hPieces++;
                hIndex++;
            } else {
                totalCost += verticalCut[vIndex] * hPieces;
                vPieces++;
                vIndex++;
            }
        }

        return totalCost;
    }
};
