class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        arr = []
        for i in horizontalCut:
            arr.append((i, 0))
        for i in verticalCut:
            arr.append((i, 1))
        arr.sort(key=lambda x:-x[0])
        # print(arr)
        vc = 0
        hc = 0
        ans = 0
        for val, cut in arr:
            # horizontal
            if cut == 0:
                ans += (vc+1) * val
                hc += 1
            # vertical
            else:
                ans += (hc+1) * val
                vc += 1
        return ans
