class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(), h.end());
        sort(v.begin(), v.end());

        long long i = 0, j = 0, p = 1, q = 1;
        long long r = 0;
        priority_queue<pair<long long, bool>> pq;

        for (int x : h) pq.push({x, true});
        for (int x : v) pq.push({x, false});

        while (!pq.empty()) {
            auto [c, ih] = pq.top();
            pq.pop();

            if (ih) {
                r += c * q;
                p++;
            } else {
                r += c * p;
                q++;
            }
        }

        return r;
    }
};
