class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hor, vector<int>& ver) {
        sort(hor.begin(), hor.end());
        sort(ver.begin(), ver.end());
        long long i = hor.size() - 1, j = ver.size() - 1, hc = 1, vc = 1, an = 0, c1 = 0, c2 = 0;
        while (i >= 0 && j >= 0) {
            if (hor[i] <= ver[j]) {
                an += ver[j] * hc;
                j--;
                vc++;
            } else {
                an += hor[i] * vc;
                i--;
                hc++;
            }
        }
        for (int k = i; k >= 0; k--) c1 += hor[k];
        for (int k = j; k >= 0; k--) c2 += ver[k];
        an += (c1 * vc) + (c2 * hc);
        return an;
    }
};
