class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hcut, vector<int>& vcut) {
        vector<pair<long long,long long>>  nums;
        // 0->hcut
        //1->vcut
        for(int i=0;i<hcut.size();i++){
            nums.push_back({hcut[i],0});
        }
        for(int i=0;i<vcut.size();i++){
            nums.push_back({vcut[i],1});
        }

        sort(nums.begin(),nums.end(),greater<pair<int,int>>());


        long long hpiece=1;
        long long vpiece=1;

        long long sum=0;
        for(auto it:nums){
            long long type=it.second;
            long long cost=it.first;

            if(type==0){
                sum+=cost*vpiece;
                hpiece++;
            }
            else{
                sum+=cost*hpiece;
                vpiece++;
            }
        }

        return sum;
    }
};
