class Solution {
public:
    long long minimumCost(long long m, long long n, vector<int>& hv, vector<int>& vv) {
        sort(hv.begin(), hv.end(), greater<int>());
        sort(vv.begin(), vv.end(), greater<int>());
        long long ans = 0;
        long long i = 0, j = 0;
        while (i < m - 1 && j < n - 1) {
            if (hv[i] > vv[j]) {
                ans += hv[i++] * (j + 1);
            } else {
                ans += vv[j++] * (i + 1);
            }
        }
        while (i < m - 1) {
            ans += hv[i++] * (j + 1);
        }
        while (j < n - 1) {
            ans += vv[j++] * (i + 1);
        }

        return ans;
    }
};
