class Solution {
public:
vector<pair<int, char>> solve(vector<int>& horizontalCuts, vector<int>& verticalCuts) {
    vector<pair<int, char>> ans;
    for (int cost : horizontalCuts) {
        ans.push_back({cost, 'H'});
    }
    for (int cost : verticalCuts) {
        ans.push_back({cost, 'V'});
    }
    sort(ans.begin(), ans.end(), greater<pair<int, char>>());
    return ans;
}
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        auto ans = solve(horizontalCut, verticalCut);
        long long res = 0;
        int hp = 1;
        int vp = 1;
        for (auto& it : ans) {
            if (it.second == 'H') {
                res += it.first * vp;
                ++hp;
            } else {
                res += it.first * hp;
                ++vp;
            }
        }
        return res;
    }
};
