class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue<pair<long long,int>>pq;
        for(int x:horizontalCut) {
            pq.push({x,0});
        }
        for(int x:verticalCut) {
            pq.push({x,1});
        }
        long long h=1,v=1;
        long long ans=0;
        while(pq.size()) {
            auto it = pq.top();
            pq.pop();
            long long cutCost = it.first;
            int d = it.second;
            if(d==0)
            {
                ans+=v*cutCost;
                h++;
            } else {
                ans+=h*cutCost;
                v++;
            }
        }
        return ans;
    }
};
