class Solution {
public:
    typedef long long ll;
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(), h.end(), [](int i, int j) {
            return i > j;
        });
        sort(v.begin(), v.end(), [](int i, int j) {
            return i > j;
        });

        long long vcnt = 0, hcnt = 0, ans = 0;
        while(vcnt < n - 1 || hcnt < m - 1) {
            if (vcnt < n - 1 && hcnt < m - 1) {
                if (h[hcnt] > v[vcnt]) {
                    ans += h[hcnt] * (vcnt + 1);
                    hcnt++;
                } else {
                    ans += v[vcnt] * (hcnt + 1);
                    vcnt++;
                }
            } else if (vcnt < n-1) {
                ans += v[vcnt] * (hcnt + 1);
                vcnt++;
            } else {
                ans += h[hcnt] * (vcnt + 1);
                hcnt++;
            }
        }
        return ans;
    }
};
