class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(), h.end());
        reverse(h.begin(), h.end());
        sort(v.begin(), v.end());
        reverse(v.begin(), v.end());
        int hp = 1, vp = 1, i = 0, j = 0;
        long long ans = 0;

        while(i < m-1 && j < n-1){
            if(h[i] >= v[j]){
                ans += h[i]*vp;
                hp++; i++;
            } 
            else{
                ans += v[j]*hp;
                vp++; j++;
            }
        }
        while(i < m-1){
            ans += h[i]*vp;
            hp++; i++;
        }
        while (j < n-1) {
            ans += v[j] * hp;
            vp++; j++;
        }

        return ans;
    }
};
