#include <vector>
#include <algorithm>
using namespace std;

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        // Sort the cuts in descending order
        sort(horizontalCut.rbegin(), horizontalCut.rend());
        sort(verticalCut.rbegin(), verticalCut.rend());

        long long total_cost = 0;
        int h_segments = 1, v_segments = 1;
        int h_index = 0, v_index = 0;

        // Process the cuts
        while (h_index < horizontalCut.size() && v_index < verticalCut.size()) {
            if (horizontalCut[h_index] >= verticalCut[v_index]) {
                total_cost += (long long)horizontalCut[h_index] * v_segments;
                h_segments++;
                h_index++;
            } else {
                total_cost += (long long)verticalCut[v_index] * h_segments;
                v_segments++;
                v_index++;
            }
        }

        // If there are remaining horizontal cuts
        while (h_index < horizontalCut.size()) {
            total_cost += (long long)horizontalCut[h_index] * v_segments;
            h_segments++;
            h_index++;
        }

        // If there are remaining vertical cuts
        while (v_index < verticalCut.size()) {
            total_cost += (long long)verticalCut[v_index] * h_segments;
            v_segments++;
            v_index++;
        }

        return total_cost;
    }
};
