class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& H, vector<int>& V) {
        vector<pair<int, int>> v;
        for (int i : H) {
            v.emplace_back(i, 0);
        }
        for (int i : V) {
            v.emplace_back(i, 1);
        }

        sort(v.rbegin(), v.rend());
        int cnth = 1, cntv = 1;
        long long ans = 0;

        for (int i = 0; i < (int) v.size(); i++) {
            if (v[i].second) {
                ans += cntv * 1LL * v[i].first;
                cnth++;
            } else {
                ans += cnth * 1LL * v[i].first;
                cntv++;
            }
        }

        return ans;
    }
};
