class Solution {
public:
    long long minimumCost(int n, int m, vector<int>& v11, vector<int>& v22) {
        n--;
        m--;
        vector<long long> v1,v2;
        for(auto i:v11) v1.push_back(i);
        for(auto i:v22) v2.push_back(i);
        long long ans=0;
        sort(v1.begin(),v1.end());
        sort(v2.begin(),v2.end());
        int i=n-1,j=m-1;
        long long cnt1=1,cnt2=1;
        while(i>=0 && j>=0){
            if(v1[i]>v2[j]){
                ans+=(long long)(v1[i]*cnt1);
                cnt2++;
                i--;
            }
            else{
                ans+=(long long)(v2[j]*cnt2);
                cnt1++;
                j--;
            }
        }
        while(i>=0){
            ans+=(long long)(v1[i]*cnt1);
            i--;
        }
        while(j>=0){
            ans+=(long long)(v2[j]*cnt2);
            j--;
        }
        return ans;
    }
};
