using namespace std;
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hc, vector<int>& vc) {
        sort(vc.rbegin(), vc.rend());
        sort(hc.rbegin(), hc.rend());

        long long hi = 0, vi = 0,hs = 1,vs = 1,ans = 0;

        while (hi < hc.size() && vi < vc.size()) {
            if (hc[hi] > vc[vi]) {
                ans += hc[hi] * vs;
                hs++,hi++;
            } 
            else 
            {
                ans += vc[vi] * hs;
                vs++,vi++;
            }
        }

        while (hi < hc.size()) {
            ans += hc[hi] * vs;
            hi++;
        }

        while (vi < vc.size()) {
            ans += vc[vi] * hs;
            vi++;
        }

        return ans;
    }
};
