class Solution:
    def minimumCost(self, m: int, n: int, horizontalCut: List[int], verticalCut: List[int]) -> int:
        a = sorted([(x, 1) for x in horizontalCut] + [(x, 2) for x in verticalCut])
        a = a[::-1]
        ans = 0
        col = row = 1
        for x, t in a:
            if t == 1:
                col += 1
                ans += x * row
            else:
                row += 1
                ans += x * col
        return ans
