class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.rbegin(),h.rend());
        sort(v.rbegin(),v.rend());
        long long ho = 0, vi= 0;
        long long h1= 1,v1= 1;
        long long ans=0;

        while (ho<h.size() and vi<v.size()) {
            if (h[ho]>v[vi]) {
                ans+=h[ho]*v1;
                h1++;
                ho++;
            } 
            else {
                ans+=v[vi] *h1;
                v1++;
                vi++;
            }
        }
        while (ho<h.size()) {
            ans+=h[ho]*v1;
            ho++;
        }

        while (vi<v.size()) {
            ans+= v[vi]*h1;
            vi++;
        }

        return ans;
    }
};
