class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(h.begin(), h.end());
        sort(v.begin(), v.end());
        long long c=0, hc = 0, vc = 0, i = m-2, j = n-2;
        while(i >= 0 && j >= 0){
            if(h[i] >= v[j]){
                c += (long long)h[i]*(vc+1);
                i--;
                hc++;
            }
            else{
                c += (long long)v[j]*(hc+1);
                j--;
                vc++;
            }
        }
        while(i >= 0){
            c += (long long)h[i]*(vc+1);
                i--;
                hc++;
        }
        while(j >= 0){
            c += (long long)v[j]*(hc+1);
                j--;
                vc++;
        }
        return c;
    }
};
