class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        sort(horizontalCut.rbegin(), horizontalCut.rend());
        sort(verticalCut.rbegin(), verticalCut.rend());

        long long i = 0, j = 0, hPieces = 1, vPieces = 1, res = 0;
        while (i < horizontalCut.size() and j < verticalCut.size()) {
            if (horizontalCut[i] > verticalCut[j]) res += horizontalCut[i++] * vPieces, hPieces++;
            else res += verticalCut[j++] * hPieces,vPieces++;
        }
        while (i < horizontalCut.size()) res += horizontalCut[i++] * vPieces;
        while (j < verticalCut.size()) res += verticalCut[j++] * hPieces;
        return res;
    }
};
