class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& hr, vector<int>& vr) {
        vector<pair<int, char>> cuts;
        for(int cst : vr) cuts.push_back({cst, 'v'});
        for(int cst : hr) cuts.push_back({cst, 'h'});
        sort(cuts.begin(), cuts.end());
        long long cnth = 1, cntv = 1, l = cuts.size();
        long long ans = 0;
        for(int i = l - 1; i >= 0; i--) {
            if(cuts[i].second == 'h') {
                cnth++;
                ans += cntv*cuts[i].first;
            }
            else {
                cntv++;
                ans += (cnth*cuts[i].first);
            }
        }
        return ans;
    }
};
