class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
         sort(horizontalCut.begin(),horizontalCut.end(),greater<int>());
        sort(verticalCut.begin(),verticalCut.end(),greater<int>());
        long long ans=0;
        int i=0;
        int j=0;
        int x=1;
        int y=1;
        while(i<m-1&&j<n-1){
            if(horizontalCut[i]<verticalCut[j]){
                ans+=verticalCut[j]*(y);
                x++;
                j++;
                // cout<<"first";

            }
            else{
                ans+=horizontalCut[i]*x;
                y++;
                i++;
                // cout<<"second";

            }


        }
        cout<<ans<<endl;
        while(i<m-1){
             ans+=horizontalCut[i]*x;
            i++;
            y++;
            // cout<<ans<<endl;


        }
        while(j<n-1){
              ans+=verticalCut[j]*(y);
                x++;
                j++;


        }
        return ans;

    }
};
