class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<int,int>> vt;
        for(auto it:horizontalCut){
            vt.push_back({it,0});
        }
        for(auto it:verticalCut){
            vt.push_back({it,1});
        }
        sort(vt.begin(),vt.end());
        long long ans=0 ,hcut=0 ,vcut=0;
        for(int i=vt.size()-1;i>=0;i--){
            auto it=vt[i];
            int a=it.first;
            int b=it.second;
            if(b==0){
                ans=ans+(long long)a*(vcut+1);
                hcut++;
            }
            else if(b==1){
                ans=ans+(long long)a*(hcut+1);
                vcut++;
            }
        }
        return ans;
    }
};
