class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        sort(v.begin(),v.end());
        sort(h.begin(),h.end());
        reverse(h.begin(),h.end());
        reverse(v.begin(),v.end());
        long long i=0,j=0,ph=1,pv=1,ans=0;
        while(i<h.size()&&j<v.size())
        {
            if(v[j]>=h[i])
            {
                ans+=v[j]*ph;
                pv++;
                j++;
            }
            else if(h[i]>v[j])
            {
                ans+=h[i]*pv;
                ph++;
                i++;
            }
            // cout<<ans<<endl;
        }
        while(j<v.size())
            ans+=v[j++]*ph;
        while(i<h.size())
            ans+=h[i++]*pv;
        return ans;
    }
};
