class Solution {
    public long minimumCost(int m, int n, int[] hCut, int[] vCut) {
        Arrays.sort(hCut);
        Arrays.sort(vCut);

        int hIndex = m-2;
        int vIndex = n-2;

        int hCount = 1, vCount = 1;
        long result = 0;

        while(hIndex >= 0 || vIndex >= 0) {
            if(hIndex < 0) {
                result += vCut[vIndex] * hCount;
                vCount++;
                vIndex--;
                continue;
            }

            if(vIndex < 0) {
                result += hCut[hIndex] * vCount;
                hCount++;
                hIndex--;
                continue;
            }

            if(hCut[hIndex] > vCut[vIndex]) {
                result += hCut[hIndex] * vCount;
                hCount++;
                hIndex--;
            } else {
                result += vCut[vIndex] * hCount;
                vCount++;
                vIndex--;
            }
        }
        return result;
    }
}
