from typing import List

class Solution:
    def minimumCost(self, m: int, n: int, hc: List[int], vc: List[int]) -> int:
        i = j = ans = 0
        hs = vs = 1

        hc.sort(reverse=True)
        vc.sort(reverse=True)

        while i < len(vc) and j < len(hc):
            if vc[i] >= hc[j]:
                ans += vs * vc[i]
                hs += 1
                i += 1
            else:
                ans += hs * hc[j]
                vs += 1
                j += 1

        while i < len(vc):
            ans += vs * vc[i]
            i += 1

        while j < len(hc):
            ans += hs * hc[j]
            j += 1

        return ans
