class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& h, vector<int>& v) {
        priority_queue<int> pv, ph;
        long long hCount = 1, vCount = 1;
        long long ans = 0;

        for(auto it:h)
            ph.push(it);
        for(auto it:v)
            pv.push(it);

        // cout << ph.size() << ",  " << pv.size() <<  " = " << ans << "\n";

        while(pv.size() || ph.size()) {
            if(pv.size() == 0) {
                // while(ph.size()) {
                    ans += ph.top()*vCount;
                    hCount++;
                    ph.pop();
                // }
            } else if(ph.size() == 0) {
                // while(pv.size()) {
                    ans += pv.top()*hCount;
                    vCount++;
                    pv.pop();
                // }
            } else {
                if(pv.top() > ph.top()) {
                    ans += pv.top()*hCount;
                    vCount++;
                    pv.pop();
                } else {
                    ans += ph.top()*vCount;
                    hCount++;
                    ph.pop();
                }
            }
            // cout << ph.size() << ",  " << pv.size() << " = " << ans << "\n";
        }
        return ans;
    }
};
