#define ll long long
class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCuts, vector<int>& verticalCuts) {
        vector<pair<int, char>> drcs;

    for (int i = 0; i < m - 1; ++i) drcs.push_back({horizontalCuts[i], 'H'});

    for (int j = 0; j < n - 1; ++j) drcs.push_back({verticalCuts[j], 'V'});

    sort(drcs.rbegin(), drcs.rend());

    ll ans = 0;
    ll h = 1;
    ll v = 1;

    for (auto& it : drcs) {
        if (it.second == 'H') {
            ans += it.first * v;
            h++;
        } else {
            ans += it.first * h;
            v++;;
        }
    }
    return ans;
    }
};
