class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        int hparts = 1, vparts = 1, i = 0, j = 0;
        long long cost = 0;
        sort(horizontalCut.begin(), horizontalCut.end(), greater<int>());
        sort(verticalCut.begin(), verticalCut.end(), greater<int>());
        m--, n--;

        while (i<m || j<n) {
            if (i<m && (j == n ||horizontalCut[i] > verticalCut[j])) {
                cost += horizontalCut[i] * hparts;
                vparts++;
                i++;
            } else if (j<n) {
                cost += verticalCut[j] * vparts;
                hparts++;
                j++;
            }
        }

        return cost;
    }
};
