#define ll long long
#define pb push_back
#define all(x) x.begin(),x.end()

class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        vector<pair<ll,char>> vp;
        for (int p:horizontalCut) vp.pb({p, 'h'});
        for (ll p:verticalCut) vp.pb({p, 'v'});

        sort(all(vp),greater<pair<int, char>>());

        ll h=1,v=1,ans=0;

        for (auto& p : vp) {
            ll fk=p.first;
            char type=p.second;
            if (type=='h') {
                ans+=(fk*v);
                h+=1;
            } else {
                ans+=(fk*h);
                v+=1;
            }
        }
        return ans;
    }
};
