class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& x, vector<int>& y) {

        int p=x.size(),q=y.size();
        sort(rbegin(x),rend(x));
        sort(rbegin(y),rend(y));

        int i=0,j=0;
        long long hcut=1,vcut=1;
        long long ans=0;

        while(i<p && j<q){

            if(x[i]>y[j]){
                ans+=(x[i]*vcut);
                hcut++,i++;
            } 

            else{
                ans+=(y[j]*hcut);
                vcut++,j++;
            }
        }

        while(i<p){
            ans+=(x[i++]*vcut);
        }
        while(j<q){
            ans+=(y[j++]*hcut);
        }

        return ans;
    }
};
