class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
         priority_queue<long long> h;
        priority_queue<long long> v;
        for(auto &t:horizontalCut) h.push(t);
        for(auto &t:verticalCut) v.push(t);
        long long count_h = 1;
        long long count_v = 1;
        long long res = 0;
        while(!h.empty() && !v.empty()){
            if(h.top() >= v.top()){
                res +=(count_h*h.top());
                h.pop();
                count_v++;
            }
            else{
                res +=(count_v*v.top());
                v.pop();
                count_h++;
            }
        }
        while(!h.empty()){
            res +=(count_h*h.top());
            h.pop();
        }
        while(!v.empty()){
            res +=(count_v*v.top());
            v.pop();
        }
        return res;

    }
};
