class Solution {
public:
    long long minimumCost(int m, int n, vector<int>& horizontalCut, vector<int>& verticalCut) {
        priority_queue< pair<long long,long long>>pq;
        long long h=1,v=1;long long cost=0;

        for(int i=0;i<m-1;i++){
            pq.push({horizontalCut[i],-1*1ll});
        }
        for(int i=0;i<n-1;i++){
            pq.push({verticalCut[i],1*1ll});
        }

        while(!pq.empty()){
            auto it=pq.top();
            pq.pop();
            long long type=it.second;

            if(type==-1){
                cost+=(h*it.first);
                v++;
            }
            else{
                cost+=(v*it.first);
                h++;
            }
        }


        return cost;
    }
};
